import React, { createContext, useContext, useState, useMemo, useEffect } from 'react';

// Create AuthContext
const AuthContext = createContext();

// Create a provider component
export const AuthProvider = ({ children }) => {
  // Initialize the currentUser state with data from localStorage (if available)
  const [currentUser, setCurrentUser] = useState(() => {
    try {
      const user = localStorage.getItem('currentUser');
      if (user && user !== "undefined") {
        const parsedUser = JSON.parse(user);
        // Ensure the parsed user object contains necessary properties like 'email' and 'role'
        if (parsedUser && parsedUser.email && parsedUser.role) {
          return parsedUser;
        }
        return null; // Return null if the necessary properties are missing
      }
      return null; // Return null if no valid user data is found
    } catch (error) {
      console.error('Error parsing currentUser from localStorage:', error);
      return null; // Return null in case of error
    }
  });

  // Log currentUser to the console whenever it changes
  useEffect(() => {
    console.log('Current User:', currentUser);
  }, [currentUser]); // Run only when currentUser changes

  // Function to log in the user
  const login = (user) => {
    try {
      // Validate user object before saving to localStorage
      if (user && user.email && user.role) {
        localStorage.setItem('currentUser', JSON.stringify(user)); // Save user data to localStorage
        setCurrentUser(user); // Update currentUser state
      } else {
        console.error('Invalid user object. Missing required properties.');
      }
    } catch (error) {
      console.error('Error saving currentUser to localStorage:', error);
    }
  };

  // Function to log out the user
  const logout = () => {
    localStorage.removeItem('currentUser'); // Remove user data from localStorage
    setCurrentUser(null); // Reset currentUser state
  };

  // Memoize the context value to avoid unnecessary re-renders
  const contextValue = useMemo(() => ({
    currentUser,
    login,
    logout,
    isAdmin: currentUser?.role === 'admin' // Conditionally check if currentUser is an admin
  }), [currentUser]); // Recalculate only when currentUser changes

  return (
    <AuthContext.Provider value={contextValue}>
      {children}
    </AuthContext.Provider>
  );
};

// Custom hook to use the AuthContext
export const useAuth = () => {
  return useContext(AuthContext);
};
